    render(){
        return(
            <div>
                <input type="text" placeholder="Enter a search term" id="inputSearchTerm"></input>
                <button type="submit" id="fetch" onClick={()=>this.fetchArticle()}>Fectch </button>
            </div>
        )
    }


}

