import React, { Component } from 'react'
import $ from 'jquery'
//use this page to test the search func
class FetchArticle extends Component{
    constructor(props){
        super()
        this.state = {}
    }

    fetchArticle(){
        var searchTerm = document.getElementById('inputSearchTerm').value
        console.log(searchTerm)

    }

    // Use hook method to execute ajax request to fetch full article content
    componentDidMount(){
        $.ajax({
            url: "https://en.wikipedia.org/w/api.php?format=json&action=query&prop=extracts&titles="+ this.searchTerm +"&redirects=true",
            headers: {
                'Access-Control-Allow-Origin' : '*',
                'Content-Type' : 'application/json'
            },
            method: 'GET',
            dataType: 'jsonp',
            data: '',
            success: function(data) {
                // this.setState({data: data});   // notice
                var dataNum = Object.keys(data.query.pages)[0]
                $('#data').empty()
                $('#data').append(`<h1>${data.query.pages[dataNum].title}</h1>`)
            }.bind(this),
            error: function(xhr, status, err) {
                console.error(this.props.url, status, err.toString());
            }.bind(this)
        });
    }
    render(){
        return(
            <div>
                <input type="text" placeholder="Enter a search term" id="inputSearchTerm"></input>
                <button type="submit" id="fetch" onClick={()=>this.fetchArticle()}>Fectch </button>
                <div id="data"></div>
            </div>
        )
    }


}

export default function ContactMe(){

    return (
        <FetchArticle />
    )
}
