import React from 'react'
import {connect} from 'react-redux'
import {createArticle} from '../../redux/actions/articleactions'
import FetchArticle from '../fetcharticle'
import {TokenAnnotator, TextAnnotator} from 'react-text-annotate'

//===================tag test=========================
const TEXT = `On Monday night , Mr. Fallon will have a co-host for the first time : The rapper Cardi B , who just released her first album, " Invasion of Privacy . "`

const TAG_COLORS = {
    ORG: '#00ffa2',
    PERSON: '#84d2ff',
  }

const Card = ({children}) => (
    <div
      style={{
        boxShadow: '0 2px 4px rgba(0,0,0,.1)',
        margin: 6,
        maxWidth: 500,
        padding: 16,
      }}
    >
      {children}
    </div>
  )
//===================tag test=========================

class CreateArticle extends React.Component {
    state = {
        title : '',
        content : '',
        value: [{start: 17, end: 19, tag: 'PERSON'}],
        tag: 'PERSON',
    }

    handleChange = (e) => {
        //console.log(e)
        this.setState({
            [e.target.id] : e.target.value,
        })
    }

    handleTagChange = (e) => {
        this.setState({tag: e.target.value})
      }

    handleSubmit = (e) => {
        e.preventDefault();
        //console.log('the article is(from createarti com): ',this.state)
        this.props.createArticle(this.state)
    }

    render() {
        return(
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white" id="createarticleform">
                <h4>New Article</h4>
                <div className="input-field">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" onChange={this.handleChange}/>
                </div>
                <div className="input-field">
                    <label htmlFor="content">Content</label>
                    <textarea id="content" className="materialize-textarea" style={{height: 500}} onChange={this.handleChange}></textarea>
                </div>
                <div className="input-field">
                    <button className="btn pink lighten-1 z-depth-0">Create</button>
                </div>
                <Card>
            <h4>Default</h4>
            <select onChange={this.handleTagChange} value={this.state.tag}>
              <option value="ORG">ORG</option>
              <option value="PERSON">PERSON</option>
            </select>
            <TokenAnnotator
              style={{
                fontFamily: 'IBM Plex Sans',
                maxWidth: 500,
                lineHeight: 1.5,
              }}
              tokens={TEXT.split(' ')}
              value={this.state.value}
              onChange={this.handleChange}
              getSpan={span => ({
                ...span,
                tag: this.state.tag,
                color: TAG_COLORS[this.state.tag],
              })}
            />
          </Card>
                <FetchArticle />
                </form>
            </div>
        );
    }

}

const mapDispatchToProps = (dispatch) => {
    return {
        createArticle: (article) => dispatch(createArticle(article))
    }
}

export default connect(null, mapDispatchToProps)(CreateArticle)
