import React from 'react'
import {connect} from 'react-redux'

import ShowArticles from './showarticles'

import {loadAllArticlesThunk} from '../redux/actions/thunks/thunks'

import {getAllArticle} from '../api/article'

// To show article lists
class Home extends React.Component {

    constructor(props){
        super(props)
    }

    render() {
        //console.log('check props from home:',this.props) :can get access
        //console.log(this.props.loadAllArticles()) //undifined

        const { articles } = this.props
        return(
            <div className="container">
                <div className="row">
                    <ShowArticles articles={articles}/>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        article: state.article.articles
    }
}

const mapDispatchToProps = {
    loadAllArticles: loadAllArticlesThunk.thunk
};

export default connect(mapStateToProps, mapDispatchToProps)(Home)
